mapper.select:
SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}

SELECT SIGN_OUT_MEMBER_NO, MEMBER_NO, MEMBER_ID, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, SIGN_OUT_MEMBER_SIGN_OUT
  FROM SIGN_OUT_MEMBER
 WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED
  FROM MEMBER
 WHERE MEMBER_EMAIL = #{memberEmail}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_PW = #{memberPw}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED
 WHERE MEMBER_NAME = #{memberName}
   AND MEMBER_EMAIL = #{memberEmail}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_PHONE = #{memberPhone}

mapper.update:
UPDATE MEMBER
   SET MEMBER_PW = #{memberPw}
 WHERE MEMBER_ID = #{memberId}
