mapper.select:
SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED, MEMBER_SESSION_ID, MEMBER_SESSION_LIMIT
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED, MEMBER_SESSION_ID, MEMBER_SESSION_LIMIT
  FROM MEMBER
 WHERE MEMBER_EMAIL = #{memberEmail}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED, MEMBER_SESSION_ID, MEMBER_SESSION_LIMIT
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_PW = #{memberPw}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED, MEMBER_SESSION_ID, MEMBER_SESSION_LIMIT
  FROM MEMBER
 WHERE MEMBER_NAME = #{memberName}
   AND MEMBER_EMAIL = #{memberEmail}

SELECT MEMBER_NO, MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_AGREE_STATE, MEMBER_SIGN_UP, MEMBER_PW_MODIFIED, MEMBER_SESSION_ID, MEMBER_SESSION_LIMIT
  FROM MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE MEMBER
   SET MEMBER_PW = #{memberPw}
 WHERE MEMBER_ID = #{memberId}
